function find($desejado, $categorias, &$finalArray) {
	foreach ($categorias as $key => $value) {
		if (is_array($value)) {
			if ($key == $desejado) {
				// return $key;
				$finalArray[] = $key; 
				return $finalArray;
			} else {
				if (find($desejado, $value, $finalArray)) {
					$finalArray[] = $key; 
					return $finalArray;
				}
			}
		} else if ($value == $desejado) {
			$finalArray[] = $value;
			//return $value;
			return $finalArray;
		}
	}
	return false;
}